import React, {Component} from 'react';
import Header from './header/header';
import Content from "./content/content";
import Footer from "./footer/footer";
import './App.scss';
import $ from "jquery";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

$(document).ready(function(){

    // sticky & expanding navbar defaults
    var navbar,navbarWidth,bodyWidth,scrollLimit,navbarSpace,factor,windowTop,calculatedAdd,maxWidth;

    navbar = $('.header .navbar');
    scrollLimit = 48; //navbar top position

    function navbarSpace(){
        navbarWidth = navbar.width();
        bodyWidth = $('body').width();
        navbarSpace = bodyWidth - navbarWidth;
    }

    $(window).on('load resize', function(){
        navbarSpace();
    });

    $(window).on('scroll', function(){

        windowTop = $(window).scrollTop()

        // expanding & sticky navbar
        factor = navbarSpace / scrollLimit;
        calculatedAdd = Math.floor(((windowTop / 100 ) * factor) * 100);
        maxWidth = navbarWidth + calculatedAdd;
        if (windowTop < scrollLimit) {
            navbar.css('max-width',maxWidth);
            navbar.removeClass('sticky');
        } else {
            navbar.css('max-width','100%');
            navbar.addClass('sticky');
        }

        // sticky messenger button
        if (windowTop > 100) {
            $('#messengerButton').css('bottom',0);
        } else {
            $('#messengerButton').css('bottom','-5rem');
        }

    });

    //dropup
    $('.quick-find__select button').on('click', function(){
        $(this).next('.quick-find__dropup').css('display','block').animate({height:'15rem'},10);
        $(this).css('visibility','hidden');
    });

    $(document).mouseup(function(e)
    {
        var container = $('.quick-find__dropup');
        if (!container.is(e.target) && container.has(e.target).length === 0)
        {
            $('.quick-find__dropup').css({display:'none',height:0});
            $('.quick-find__select button').css('visibility','visible');
        }
    });

    //sectors
    $('.sectors__slider').slick({
        arrows:false,
        slidesToShow: 2,
        slidesToScroll: 1,
        infiniteScroll: true,
        responsive: [
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    });

    //testimonials
    $('.testimonials__slider').slick({
        slidesToShow: 1,
        slidesToScroll: 1,
        prevArrow:'<button class="prev"><img src="/img/icon/back.svg" height="24"></button>',
        nextArrow:'<button class="next"><img src="/img/icon/next.svg" height="24"></button>',
        speed: 1000,
        fade: true,
        adaptiveHeight: true
    });

});



class App extends Component {
  render() {

    return (
        <section>
            <Header />
            <Content />
            <Footer />
        </section>

    );
  }
}



export default App;

